class Todo{constructor(item,finished=!1){this.item=item,this.finished=finished}changeState(){this.finished=!this.finished}}class TodoManager{constructor(){this.todoList=[]}addItem(item,finished=!1){this.todoList.push(new Todo(item,finished))}getItems(){return this.todoList}getLeftTodoCount(){this.todoList.reduce((total,current)=>!1===current.finished?++total:total,0)}}const todoManager=new TodoManager;todoManager.addItem("아침인사하기",2342);